DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(8) NOT NULL AUTO_INCREMENT COMMENT '用户的id',
  `name` varchar(15) NOT NULL COMMENT '用户的名字',
  `email` varchar(50) DEFAULT NULL COMMENT '用户的邮箱',
  `login_password` varchar(50) DEFAULT NULL COMMENT '登录密码',
  `phone` varchar(15) DEFAULT NULL COMMENT '手机号',
  `card_id` varchar(20) DEFAULT NULL COMMENT '身份证',
  `secret_id` int(10) DEFAULT NULL COMMENT '密保表的id',
  `registration` timestamp NULL DEFAULT '0000-00-00 00:00:00' ON UPDATE CURRENT_TIMESTAMP COMMENT '用户的注册时间',
  `state` int(1) DEFAULT '0',
  `balance` double(50,0) DEFAULT NULL,
  `sufface` varchar(100) DEFAULT NULL,
  `delete_state` int(1) DEFAULT NULL COMMENT '是否删除',
  PRIMARY KEY (`id`),
  UNIQUE KEY `user_email` (`email`),
  UNIQUE KEY `user_call` (`phone`),
  KEY `user_sercret_id` (`secret_id`),
  CONSTRAINT `secret_fk` FOREIGN KEY (`secret_id`) REFERENCES `secret` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=61 DEFAULT CHARSET=utf8;

